// ========================================
// Database Configuration (MongoDB)
// ========================================

const mongoose = require('mongoose');

// In-memory storage for development without MongoDB
const inMemoryDB = {
    bookings: [],
    users: []
};

const connectDB = async () => {
    const mongoUri = process.env.MONGODB_URI;

    // Skip MongoDB connection if no URI provided (development mode)
    if (!mongoUri) {
        console.log('⚠ No MONGODB_URI provided - running in demo mode with in-memory storage');
        console.log('  Data will not persist between restarts');
        return;
    }

    try {
        const options = {
            maxPoolSize: 10,
            serverSelectionTimeoutMS: 5000,
            socketTimeoutMS: 45000,
        };

        await mongoose.connect(mongoUri, options);

        console.log(`MongoDB Connected: ${mongoose.connection.host}`);

        // Handle connection events
        mongoose.connection.on('error', (err) => {
            console.error('MongoDB connection error:', err);
        });

        mongoose.connection.on('disconnected', () => {
            console.warn('MongoDB disconnected. Attempting to reconnect...');
        });

        mongoose.connection.on('reconnected', () => {
            console.log('MongoDB reconnected');
        });

    } catch (error) {
        console.error('MongoDB connection failed:', error.message);
        console.log('Falling back to in-memory storage for development');
        // Exit process with failure in production
        if (process.env.NODE_ENV === 'production') {
            process.exit(1);
        }
    }
};

// Check if MongoDB is connected
const isConnected = () => {
    return mongoose.connection.readyState === 1;
};

module.exports = connectDB;
module.exports.isConnected = isConnected;
module.exports.inMemoryDB = inMemoryDB;

