    15→    --dark-light: #597B87;        /* Pantone 645 C */
    16→    --gray-900: #183251;          /* Deep navy */
    17→    --gray-800: #2a4a66;          /* Darker blend */
    18→    --gray-700: #6c747c;          /* Cool Gray 9 C - Body text */
    19→    --gray-600: #6c747c;          /* Cool Gray 9 C */
    20→    --gray-500: #597B87;          /* Pantone 645 C */
    21→    --gray-400: #c4c4c4;          /* Pantone 424 C - Light neutral */
    22→    --gray-300: #c4cbdb;          /* Pantone 538 C */
    23→    --gray-200: #e0e4eb;          /* Lighter blend */
    24→    --gray-100: #f0f2f5;          /* Very light */
    25→    --gray-50: #f7f8fa;           /* Near white */
    26→    --white: #ffffff;
    27→    --gradient: linear-gradient(135deg, var(--primary-dark) 0%, var(--primary) 100%);
    28→    --gradient-dark: linear-gradient(135deg, var(--dark) 0%, #2a4a66 100%);
    29→    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    30→    --shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
    31→    --shadow-md: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
    32→    --shadow-lg: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
    33→    --shadow-xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
    34→    --radius-sm: 0.375rem;
    35→    --radius: 0.5rem;
    36→    --radius-lg: 0.75rem;
    37→    --radius-xl: 1rem;
    38→    --radius-2xl: 1.5rem;
    39→    --radius-full: 9999px;
    40→    --transition: all 0.3s ease;
    41→}
    42→
    43→/* Reset & Base */
    44→*, *::before, *::after {
    45→    margin: 0;
    46→    padding: 0;
    47→    box-sizing: border-box;
    48→}
