/* ========================================
   SHUTTLE PLUS - Modern Ride Sharing Website
   ======================================== */

/* CSS Variables - Shuttle Plus Brand Palette */
:root {
    /* Brand Colors */
    --primary: #597B87;           /* Pantone 645 C - Muted blue-gray */
    --primary-dark: #183251;      /* Pantone 540 C - Deep navy (anchor color) */
    --primary-light: #c4cbdb;     /* Pantone 538 C - Light gray-blue */
    --secondary: #597B87;         /* Pantone 645 C - Accent */
    --accent: #00b4d8;            /* Cyan for logo emphasis */
    --success: #10b981;
    --dark: #183251;              /* Pantone 540 C - Deep navy */
    --dark-light: #597B87;        /* Pantone 645 C */
    --gray-900: #183251;          /* Deep navy */
    --gray-800: #2a4a66;          /* Darker blend */
    --gray-700: #6c747c;          /* Cool Gray 9 C - Body text */
    --gray-600: #6c747c;          /* Cool Gray 9 C */
    --gray-500: #597B87;          /* Pantone 645 C */
    --gray-400: #c4c4c4;          /* Pantone 424 C - Light neutral */
    --gray-300: #c4cbdb;          /* Pantone 538 C */
    --gray-200: #e0e4eb;          /* Lighter blend */
    --gray-100: #f0f2f5;          /* Very light */
    --gray-50: #f7f8fa;           /* Near white */
    --white: #ffffff;
    --gradient: linear-gradient(135deg, var(--primary-dark) 0%, var(--primary) 100%);
    --gradient-dark: linear-gradient(135deg, var(--dark) 0%, #2a4a66 100%);
    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    --shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
    --shadow-md: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
    --shadow-lg: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
    --shadow-xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
    --radius-sm: 0.375rem;
    --radius: 0.5rem;
    --radius-lg: 0.75rem;
    --radius-xl: 1rem;
    --radius-2xl: 1.5rem;
    --radius-full: 9999px;
    --transition: all 0.3s ease;
}

/* Reset & Base */
*, *::before, *::after {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    font-size: 16px;
    line-height: 1.6;
    color: var(--gray-700);
    background-color: var(--white);
    overflow-x: hidden;
}

a {
    text-decoration: none;
    color: inherit;
    transition: var(--transition);
}

ul {
    list-style: none;
}

img {
    max-width: 100%;
    height: auto;
}

/* Container */
.container {
    width: 100%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1.5rem;
}

/* Buttons */
.btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    padding: 0.875rem 1.75rem;
    font-size: 0.9375rem;
    font-weight: 600;
    border-radius: var(--radius-lg);
    cursor: pointer;
    transition: var(--transition);
    border: 2px solid transparent;
    white-space: nowrap;
}

.btn-primary {
    background: var(--gradient);
    color: var(--white);
    box-shadow: 0 4px 14px 0 rgba(24, 50, 81, 0.4);
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px 0 rgba(24, 50, 81, 0.5);
}

.btn-outline {
    background: transparent;
    color: var(--primary);
    border-color: var(--primary);
}

.btn-outline:hover {
    background: var(--primary);
    color: var(--white);
}

.btn-white {
    background: var(--white);
    color: var(--primary);
}

.btn-white:hover {
    background: var(--gray-100);
    transform: translateY(-2px);
}

.btn-outline-white {
    background: transparent;
    color: var(--white);
    border-color: var(--white);
}

.btn-outline-white:hover {
    background: var(--white);
    color: var(--primary);
}

.btn-block {
    width: 100%;
}

/* Navigation */
.navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 1000;
    padding: 1rem 0;
    transition: var(--transition);
}

.navbar.scrolled {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    box-shadow: var(--shadow);
    padding: 0.75rem 0;
}

.nav-container {
    display: flex;
    align-items: center;
    justify-content: space-between;
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 2rem;
}

.logo {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 1.5rem;
    font-weight: 800;
    color: var(--white);
}

.navbar.scrolled .logo {
    color: var(--dark);
}

.logo-icon {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
    background: var(--gradient);
    border-radius: var(--radius-lg);
    color: var(--white);
}

.logo-accent {
    color: var(--primary);
}

.navbar.scrolled .logo-accent {
    color: var(--primary);
}

/* Logo Image Styles */
.logo-img {
    height: 45px;
    width: auto;
    object-fit: contain;
}

.navbar.scrolled .logo-img {
    height: 40px;
}

.footer-logo-img {
    height: 50px;
    margin-bottom: 1rem;
}

.nav-links {
    display: flex;
    align-items: center;
    gap: 2rem;
}

.nav-links a {
    font-weight: 500;
    color: rgba(255, 255, 255, 0.9);
    position: relative;
}

.navbar.scrolled .nav-links a {
    color: var(--gray-700);
}

.nav-links a::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--gradient);
    transition: var(--transition);
}

.nav-links a:hover::after,
.nav-links a.active::after {
    width: 100%;
}

.nav-buttons {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.nav-buttons .btn-outline {
    color: var(--white);
    border-color: rgba(255, 255, 255, 0.5);
}

.navbar.scrolled .nav-buttons .btn-outline {
    color: var(--primary);
    border-color: var(--primary);
}

.nav-buttons .btn-outline:hover {
    background: var(--white);
    color: var(--primary);
    border-color: var(--white);
}

.navbar.scrolled .nav-buttons .btn-outline:hover {
    background: var(--primary);
    color: var(--white);
}

.mobile-menu-btn {
    display: none;
    flex-direction: column;
    gap: 5px;
    background: none;
    border: none;
    cursor: pointer;
    padding: 5px;
}

.mobile-menu-btn span {
    width: 25px;
    height: 2px;
    background: var(--white);
    transition: var(--transition);
}

.navbar.scrolled .mobile-menu-btn span {
    background: var(--dark);
}

/* Hero Section */
.hero {
    position: relative;
    min-height: 100vh;
    display: flex;
    align-items: center;
    padding: 6rem 0 4rem;
    overflow: hidden;
}

.hero-bg {
    position: absolute;
    inset: 0;
    background: var(--gradient-dark);
    z-index: -1;
}

.hero-gradient {
    position: absolute;
    inset: 0;
    background:
        radial-gradient(ellipse at 20% 50%, rgba(89, 123, 135, 0.3) 0%, transparent 50%),
        radial-gradient(ellipse at 80% 20%, rgba(0, 180, 216, 0.2) 0%, transparent 40%),
        radial-gradient(ellipse at 60% 80%, rgba(196, 203, 219, 0.15) 0%, transparent 40%);
}

.floating-shapes {
    position: absolute;
    inset: 0;
    overflow: hidden;
}

.shape {
    position: absolute;
    border-radius: 50%;
    background: linear-gradient(135deg, rgba(89, 123, 135, 0.15) 0%, rgba(0, 180, 216, 0.1) 100%);
    animation: float 20s ease-in-out infinite;
}

.shape-1 {
    width: 400px;
    height: 400px;
    top: -100px;
    right: -100px;
    animation-delay: 0s;
}

.shape-2 {
    width: 300px;
    height: 300px;
    bottom: -50px;
    left: -50px;
    animation-delay: -5s;
}

.shape-3 {
    width: 200px;
    height: 200px;
    top: 50%;
    left: 30%;
    animation-delay: -10s;
}

@keyframes float {
    0%, 100% { transform: translateY(0) rotate(0deg); }
    50% { transform: translateY(-30px) rotate(180deg); }
}

.hero-container {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: center;
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 2rem;
}

.hero-content {
    color: var(--white);
}

.hero-badge {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.5rem 1rem;
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: var(--radius-full);
    font-size: 0.875rem;
    margin-bottom: 1.5rem;
    backdrop-filter: blur(10px);
}

.hero-badge i {
    color: var(--accent);
}

.hero-title {
    font-size: 3.75rem;
    font-weight: 800;
    line-height: 1.1;
    margin-bottom: 1.5rem;
}

.gradient-text {
    background: linear-gradient(135deg, var(--primary-light) 0%, var(--accent) 50%, #00d4ff 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.hero-subtitle {
    font-size: 1.25rem;
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 2.5rem;
    max-width: 500px;
}

.hero-stats {
    display: flex;
    gap: 3rem;
}

.stat {
    text-align: left;
}

.stat-number {
    font-size: 2.5rem;
    font-weight: 800;
    color: var(--white);
}

.stat-suffix {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--primary-light);
}

.stat-label {
    display: block;
    font-size: 0.875rem;
    color: rgba(255, 255, 255, 0.6);
    margin-top: 0.25rem;
}

/* Booking Card */
.hero-booking {
    display: flex;
    justify-content: flex-end;
}

.booking-card {
    background: var(--white);
    border-radius: var(--radius-2xl);
    padding: 2rem;
    width: 100%;
    max-width: 420px;
    box-shadow: var(--shadow-xl);
}

.booking-header {
    margin-bottom: 1.5rem;
}

.booking-header h3 {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--dark);
    margin-bottom: 1rem;
}

.booking-tabs {
    display: flex;
    gap: 0.5rem;
    background: var(--gray-100);
    padding: 0.25rem;
    border-radius: var(--radius-lg);
}

.tab-btn {
    flex: 1;
    padding: 0.625rem 1rem;
    font-size: 0.875rem;
    font-weight: 500;
    color: var(--gray-600);
    background: transparent;
    border: none;
    border-radius: var(--radius);
    cursor: pointer;
    transition: var(--transition);
}

.tab-btn.active {
    background: var(--white);
    color: var(--primary);
    box-shadow: var(--shadow-sm);
}

.booking-form .form-group {
    margin-bottom: 1rem;
}

.booking-form label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.8125rem;
    font-weight: 500;
    color: var(--gray-600);
    margin-bottom: 0.5rem;
}

.booking-form label i {
    color: var(--primary);
    font-size: 0.875rem;
}

.booking-form input,
.booking-form select {
    width: 100%;
    padding: 0.875rem 1rem;
    font-size: 0.9375rem;
    color: var(--dark);
    background: var(--gray-50);
    border: 2px solid var(--gray-200);
    border-radius: var(--radius-lg);
    transition: var(--transition);
}

.booking-form input:focus,
.booking-form select:focus {
    outline: none;
    border-color: var(--primary);
    background: var(--white);
    box-shadow: 0 0 0 4px rgba(99, 102, 241, 0.1);
}

.form-row {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1rem;
}

.booking-form .btn {
    margin-top: 0.5rem;
}

.booking-note {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    margin-top: 1rem;
    font-size: 0.8125rem;
    color: var(--gray-500);
}

.booking-note i {
    color: var(--success);
}

/* Hero Scroll */
.hero-scroll {
    position: absolute;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
}

.hero-scroll a {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
    color: rgba(255, 255, 255, 0.6);
    font-size: 0.8125rem;
    animation: bounce 2s ease-in-out infinite;
}

@keyframes bounce {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(8px); }
}

/* Partners Section */
.partners {
    padding: 3rem 0;
    background: var(--gray-50);
    border-bottom: 1px solid var(--gray-200);
}

.partners-label {
    text-align: center;
    font-size: 0.875rem;
    color: var(--gray-500);
    margin-bottom: 1.5rem;
}

.partners-logos {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 3rem;
    flex-wrap: wrap;
}

.partner-logo {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 1.125rem;
    font-weight: 600;
    color: var(--gray-400);
    transition: var(--transition);
}

.partner-logo:hover {
    color: var(--gray-600);
}

.partner-logo i {
    font-size: 1.25rem;
}

/* Section Styles */
section {
    padding: 5rem 0;
}

.section-header {
    text-align: center;
    max-width: 600px;
    margin: 0 auto 3rem;
}

.section-tag {
    display: inline-block;
    padding: 0.375rem 1rem;
    font-size: 0.8125rem;
    font-weight: 600;
    color: var(--primary);
    background: rgba(99, 102, 241, 0.1);
    border-radius: var(--radius-full);
    margin-bottom: 1rem;
}

.section-title {
    font-size: 2.5rem;
    font-weight: 800;
    color: var(--dark);
    margin-bottom: 1rem;
}

.section-subtitle {
    font-size: 1.125rem;
    color: var(--gray-500);
}

/* Services Section */
.services {
    background: var(--white);
}

.services-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 1.5rem;
}

.service-card {
    background: var(--white);
    border: 1px solid var(--gray-200);
    border-radius: var(--radius-xl);
    padding: 2rem;
    transition: var(--transition);
}

.service-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--shadow-lg);
    border-color: transparent;
}

.service-card.featured {
    background: var(--gradient);
    border-color: transparent;
    color: var(--white);
}

.service-card.featured .service-icon {
    background: rgba(255, 255, 255, 0.2);
    color: var(--white);
}

.service-card.featured h3,
.service-card.featured p,
.service-card.featured li {
    color: var(--white);
}

.service-card.featured .service-link {
    color: var(--white);
}

.service-icon {
    width: 60px;
    height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(24, 50, 81, 0.1);
    border-radius: var(--radius-lg);
    margin-bottom: 1.5rem;
    font-size: 1.5rem;
    color: var(--primary-dark);
}

.service-card h3 {
    font-size: 1.25rem;
    font-weight: 700;
    color: var(--dark);
    margin-bottom: 0.75rem;
}

.service-card p {
    font-size: 0.9375rem;
    color: var(--gray-600);
    margin-bottom: 1.25rem;
}

.service-features {
    margin-bottom: 1.5rem;
}

.service-features li {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.875rem;
    color: var(--gray-600);
    margin-bottom: 0.5rem;
}

.service-features li i {
    color: var(--success);
    font-size: 0.75rem;
}

.service-link {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    font-weight: 600;
    color: var(--primary);
}

.service-link i {
    transition: var(--transition);
}

.service-link:hover i {
    transform: translateX(4px);
}

/* How It Works */
.how-it-works {
    background: var(--gray-50);
}

.steps-container {
    display: flex;
    align-items: flex-start;
    justify-content: center;
    gap: 0;
}

.step {
    flex: 1;
    text-align: center;
    padding: 0 2rem;
    max-width: 300px;
}

.step-number {
    font-size: 3rem;
    font-weight: 800;
    color: var(--gray-200);
    margin-bottom: 1rem;
}

.step-icon {
    width: 80px;
    height: 80px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: var(--gradient);
    border-radius: var(--radius-xl);
    margin: 0 auto 1.5rem;
    font-size: 2rem;
    color: var(--white);
    box-shadow: 0 10px 30px -10px rgba(24, 50, 81, 0.5);
}

.step h3 {
    font-size: 1.25rem;
    font-weight: 700;
    color: var(--dark);
    margin-bottom: 0.75rem;
}

.step p {
    font-size: 0.9375rem;
    color: var(--gray-600);
}

.step-connector {
    display: flex;
    align-items: center;
    padding-top: 100px;
}

.connector-line {
    width: 60px;
    height: 2px;
    background: var(--gray-300);
}

.connector-dot {
    width: 8px;
    height: 8px;
    background: var(--primary);
    border-radius: 50%;
}

/* Fleet Section */
.fleet {
    background: var(--white);
}

.fleet-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 1.5rem;
}

.fleet-card {
    background: var(--white);
    border: 1px solid var(--gray-200);
    border-radius: var(--radius-xl);
    overflow: hidden;
    transition: var(--transition);
}

.fleet-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--shadow-lg);
}

.fleet-card.featured {
    border-color: var(--primary);
    box-shadow: 0 0 0 1px var(--primary);
}

.fleet-image {
    position: relative;
    height: 180px;
    background: var(--gray-100);
    display: flex;
    align-items: center;
    justify-content: center;
}

.fleet-placeholder {
    font-size: 4rem;
    color: var(--gray-300);
}

.fleet-badge {
    position: absolute;
    top: 1rem;
    right: 1rem;
    padding: 0.375rem 0.75rem;
    font-size: 0.75rem;
    font-weight: 600;
    color: var(--white);
    background: var(--primary);
    border-radius: var(--radius-full);
}

.fleet-badge.luxury {
    background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
}

.fleet-info {
    padding: 1.5rem;
}

.fleet-info h3 {
    font-size: 1.25rem;
    font-weight: 700;
    color: var(--dark);
    margin-bottom: 0.5rem;
}

.fleet-info p {
    font-size: 0.875rem;
    color: var(--gray-500);
    margin-bottom: 1rem;
}

.fleet-specs {
    display: flex;
    gap: 1rem;
    margin-bottom: 1rem;
}

.fleet-specs span {
    display: flex;
    align-items: center;
    gap: 0.375rem;
    font-size: 0.8125rem;
    color: var(--gray-600);
}

.fleet-specs i {
    color: var(--primary);
}

.fleet-price {
    margin-bottom: 1rem;
}

.price-from {
    font-size: 0.8125rem;
    color: var(--gray-500);
}

.price-amount {
    font-size: 1.75rem;
    font-weight: 800;
    color: var(--dark);
    margin-left: 0.25rem;
}

/* Features Section */
.features {
    background: var(--dark);
    padding: 4rem 0;
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 2rem;
}

.feature {
    text-align: center;
}

.feature-icon {
    width: 60px;
    height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(255, 255, 255, 0.1);
    border-radius: var(--radius-lg);
    margin: 0 auto 1rem;
    font-size: 1.5rem;
    color: var(--accent);
}

.feature h3 {
    font-size: 1.125rem;
    font-weight: 600;
    color: var(--white);
    margin-bottom: 0.5rem;
}

.feature p {
    font-size: 0.875rem;
    color: var(--gray-400);
}

/* Testimonials */
.testimonials {
    background: var(--gray-50);
}

.testimonials-slider {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 1.5rem;
}

.testimonial-card {
    background: var(--white);
    border-radius: var(--radius-xl);
    padding: 2rem;
    box-shadow: var(--shadow);
}

.testimonial-rating {
    display: flex;
    gap: 0.25rem;
    margin-bottom: 1rem;
}

.testimonial-rating i {
    color: var(--accent);
    font-size: 0.875rem;
}

.testimonial-text {
    font-size: 1rem;
    color: var(--gray-700);
    line-height: 1.7;
    margin-bottom: 1.5rem;
}

.testimonial-author {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.author-avatar {
    width: 48px;
    height: 48px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: var(--gray-100);
    border-radius: 50%;
    color: var(--gray-400);
}

.author-info h4 {
    font-size: 1rem;
    font-weight: 600;
    color: var(--dark);
}

.author-info span {
    font-size: 0.8125rem;
    color: var(--gray-500);
}

/* App Section */
.app-section {
    background: var(--white);
    overflow: hidden;
}

.app-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: center;
}

.app-info h2 {
    font-size: 2.5rem;
    font-weight: 800;
    color: var(--dark);
    margin-bottom: 1rem;
}

.app-info p {
    font-size: 1.125rem;
    color: var(--gray-600);
    margin-bottom: 2rem;
}

.app-features {
    display: flex;
    gap: 2rem;
    margin-bottom: 2rem;
}

.app-feature {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-weight: 500;
    color: var(--gray-700);
}

.app-feature i {
    color: var(--primary);
}

.app-buttons {
    display: flex;
    gap: 1rem;
}

.app-btn {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.75rem 1.5rem;
    background: var(--dark);
    color: var(--white);
    border-radius: var(--radius-lg);
    transition: var(--transition);
}

.app-btn:hover {
    background: var(--gray-800);
    transform: translateY(-2px);
}

.app-btn i {
    font-size: 1.75rem;
}

.app-btn span {
    font-size: 0.75rem;
    color: var(--gray-400);
}

.app-btn strong {
    display: block;
    font-size: 1rem;
}

/* Phone Mockup */
.app-mockup {
    display: flex;
    justify-content: center;
}

.phone-frame {
    width: 280px;
    height: 580px;
    background: var(--dark);
    border-radius: 40px;
    padding: 12px;
    box-shadow: var(--shadow-xl);
}

.phone-screen {
    width: 100%;
    height: 100%;
    background: var(--white);
    border-radius: 32px;
    overflow: hidden;
}

.mock-app {
    height: 100%;
    display: flex;
    flex-direction: column;
}

.mock-header {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 1rem;
    background: var(--gradient);
    color: var(--white);
    font-weight: 600;
}

.mock-map {
    flex: 1;
    background: linear-gradient(180deg, #e8f4f8 0%, #d4e8f0 100%);
    position: relative;
}

.mock-map::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 100px;
    height: 100px;
    background: rgba(99, 102, 241, 0.2);
    border-radius: 50%;
}

.mock-card {
    padding: 1.25rem;
    background: var(--white);
    border-radius: 20px 20px 0 0;
    box-shadow: 0 -10px 30px rgba(0, 0, 0, 0.1);
}

.mock-input {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.75rem;
    background: var(--gray-100);
    border-radius: var(--radius);
    margin-bottom: 0.75rem;
    font-size: 0.875rem;
    color: var(--gray-500);
}

.mock-input i {
    font-size: 0.625rem;
    color: var(--primary);
}

.mock-button {
    padding: 0.875rem;
    background: var(--gradient);
    color: var(--white);
    border-radius: var(--radius);
    text-align: center;
    font-weight: 600;
    font-size: 0.875rem;
}

/* CTA Section */
.cta {
    background: var(--gradient);
    padding: 5rem 0;
}

.cta-content {
    text-align: center;
    color: var(--white);
}

.cta-content h2 {
    font-size: 2.5rem;
    font-weight: 800;
    margin-bottom: 1rem;
}

.cta-content p {
    font-size: 1.25rem;
    opacity: 0.9;
    margin-bottom: 2rem;
}

.cta-buttons {
    display: flex;
    justify-content: center;
    gap: 1rem;
}

/* Contact Section */
.contact {
    background: var(--white);
}

.contact-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
}

.contact-info h2 {
    font-size: 2rem;
    font-weight: 800;
    color: var(--dark);
    margin-bottom: 1rem;
}

.contact-info > p {
    font-size: 1rem;
    color: var(--gray-600);
    margin-bottom: 2rem;
}

.contact-details {
    margin-bottom: 2rem;
}

.contact-item {
    display: flex;
    align-items: flex-start;
    gap: 1rem;
    margin-bottom: 1.5rem;
}

.contact-icon {
    width: 48px;
    height: 48px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(99, 102, 241, 0.1);
    border-radius: var(--radius-lg);
    color: var(--primary);
    flex-shrink: 0;
}

.contact-item h4 {
    font-size: 1rem;
    font-weight: 600;
    color: var(--dark);
    margin-bottom: 0.25rem;
}

.contact-item p {
    font-size: 0.9375rem;
    color: var(--gray-600);
}

.social-links {
    display: flex;
    gap: 0.75rem;
}

.social-links a {
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: var(--gray-100);
    border-radius: var(--radius);
    color: var(--gray-600);
    transition: var(--transition);
}

.social-links a:hover {
    background: var(--primary);
    color: var(--white);
}

/* Contact Form */
.contact-form-wrapper {
    background: var(--gray-50);
    border-radius: var(--radius-xl);
    padding: 2rem;
}

.contact-form .form-group {
    margin-bottom: 1rem;
}

.contact-form input,
.contact-form textarea {
    width: 100%;
    padding: 0.875rem 1rem;
    font-size: 0.9375rem;
    font-family: inherit;
    color: var(--dark);
    background: var(--white);
    border: 2px solid var(--gray-200);
    border-radius: var(--radius-lg);
    transition: var(--transition);
    resize: vertical;
}

.contact-form input:focus,
.contact-form textarea:focus {
    outline: none;
    border-color: var(--primary);
    box-shadow: 0 0 0 4px rgba(99, 102, 241, 0.1);
}

.contact-form .btn {
    margin-top: 0.5rem;
}

/* Footer */
.footer {
    background: var(--dark);
    padding: 4rem 0 0;
}

.footer-grid {
    display: grid;
    grid-template-columns: 2fr 1fr 1fr 1fr;
    gap: 3rem;
    padding-bottom: 3rem;
    border-bottom: 1px solid var(--gray-800);
}

.footer-brand .logo {
    margin-bottom: 1rem;
}

.footer-brand p {
    font-size: 0.9375rem;
    color: var(--gray-400);
    margin-bottom: 1.5rem;
    max-width: 300px;
}

.footer-badges {
    display: flex;
    flex-wrap: wrap;
    gap: 0.75rem;
}

.footer-badges .badge {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.5rem 0.75rem;
    font-size: 0.75rem;
    color: var(--gray-400);
    background: var(--gray-800);
    border-radius: var(--radius);
}

.footer-links h4 {
    font-size: 1rem;
    font-weight: 600;
    color: var(--white);
    margin-bottom: 1.5rem;
}

.footer-links ul li {
    margin-bottom: 0.75rem;
}

.footer-links a {
    font-size: 0.9375rem;
    color: var(--gray-400);
}

.footer-links a:hover {
    color: var(--white);
}

.footer-bottom {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 0;
}

.footer-bottom p {
    font-size: 0.875rem;
    color: var(--gray-500);
}

.payment-methods {
    display: flex;
    gap: 1rem;
    font-size: 1.5rem;
    color: var(--gray-500);
}

/* Why Us / Comparison Section */
.why-us {
    background: var(--gray-50);
    padding: 5rem 0;
}

.comparison-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 2rem;
    max-width: 900px;
    margin: 0 auto;
}

.comparison-card {
    background: var(--white);
    border-radius: var(--radius-xl);
    padding: 2rem;
    box-shadow: var(--shadow);
}

.comparison-card h3 {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    font-size: 1.25rem;
    font-weight: 700;
    margin-bottom: 1.5rem;
}

.comparison-card.problem {
    border: 2px solid var(--gray-300);
}

.comparison-card.problem h3 {
    color: #dc2626;
}

.comparison-card.problem h3 i {
    color: #dc2626;
}

.comparison-card.solution {
    border: 2px solid var(--primary);
    background: linear-gradient(135deg, rgba(24, 50, 81, 0.02) 0%, rgba(89, 123, 135, 0.05) 100%);
}

.comparison-card.solution h3 {
    color: var(--primary-dark);
}

.comparison-card.solution h3 i {
    color: var(--success);
}

.comparison-card ul {
    list-style: none;
}

.comparison-card ul li {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.75rem 0;
    border-bottom: 1px solid var(--gray-100);
    font-size: 0.9375rem;
    color: var(--gray-700);
}

.comparison-card ul li:last-child {
    border-bottom: none;
}

.comparison-card.problem ul li i {
    color: #dc2626;
    font-size: 0.75rem;
}

.comparison-card.solution ul li i {
    color: var(--success);
    font-size: 0.75rem;
}

/* Destinations Section */
.destinations {
    background: var(--white);
    padding: 5rem 0;
}

.destinations-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 1.5rem;
}

.destination-card {
    display: flex;
    align-items: center;
    gap: 1rem;
    background: var(--white);
    border: 1px solid var(--gray-200);
    border-radius: var(--radius-xl);
    padding: 1.5rem;
    transition: var(--transition);
}

.destination-card:hover {
    border-color: var(--primary);
    box-shadow: var(--shadow-md);
    transform: translateY(-4px);
}

.destination-icon {
    width: 50px;
    height: 50px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(24, 50, 81, 0.1);
    border-radius: var(--radius-lg);
    color: var(--primary-dark);
    font-size: 1.25rem;
    flex-shrink: 0;
}

.destination-info {
    flex: 1;
    min-width: 0;
}

.destination-info h3 {
    font-size: 1rem;
    font-weight: 700;
    color: var(--dark);
    margin-bottom: 0.25rem;
}

.destination-info p {
    font-size: 0.8125rem;
    color: var(--gray-500);
    margin-bottom: 0.25rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.destination-info .distance {
    display: flex;
    align-items: center;
    gap: 0.375rem;
    font-size: 0.75rem;
    color: var(--gray-400);
}

.destination-info .distance i {
    font-size: 0.625rem;
}

.destination-price {
    text-align: right;
    flex-shrink: 0;
}

.destination-price .price-label {
    display: block;
    font-size: 0.75rem;
    color: var(--gray-500);
}

.destination-price .price-amount {
    font-size: 1.25rem;
    font-weight: 800;
    color: var(--primary-dark);
}

.destinations-note {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    margin-top: 2rem;
    padding: 1rem;
    background: var(--gray-50);
    border-radius: var(--radius-lg);
    font-size: 0.875rem;
    color: var(--gray-600);
}

.destinations-note i {
    color: var(--primary);
}

/* Corporate Section */
.corporate {
    background: var(--gray-50);
    padding: 5rem 0;
}

.corporate-content {
    display: grid;
    grid-template-columns: 1.2fr 0.8fr;
    gap: 4rem;
    align-items: center;
}

.corporate-info h2 {
    font-size: 2rem;
    font-weight: 800;
    color: var(--dark);
    margin-bottom: 1rem;
}

.corporate-info > p {
    font-size: 1.0625rem;
    color: var(--gray-600);
    margin-bottom: 2rem;
}

.corporate-features {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.corp-feature {
    display: flex;
    gap: 1rem;
}

.corp-feature i {
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(24, 50, 81, 0.1);
    border-radius: var(--radius);
    color: var(--primary-dark);
    font-size: 1rem;
    flex-shrink: 0;
}

.corp-feature h4 {
    font-size: 0.9375rem;
    font-weight: 600;
    color: var(--dark);
    margin-bottom: 0.25rem;
}

.corp-feature p {
    font-size: 0.8125rem;
    color: var(--gray-500);
}

.corporate-clients {
    background: var(--white);
    border-radius: var(--radius-xl);
    padding: 2rem;
    box-shadow: var(--shadow);
}

.corporate-clients h3 {
    font-size: 1rem;
    font-weight: 600;
    color: var(--gray-500);
    margin-bottom: 1.5rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

.client-logos {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.client-logo {
    padding: 1rem;
    background: var(--gray-50);
    border-radius: var(--radius);
    font-size: 0.875rem;
    font-weight: 500;
    color: var(--gray-600);
    text-align: center;
}

/* Responsive Design */
@media (max-width: 1200px) {
    .services-grid,
    .fleet-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .features-grid {
        grid-template-columns: repeat(2, 1fr);
        gap: 2rem;
    }

    .destinations-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .corporate-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }
}

@media (max-width: 992px) {
    .hero-container {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .hero-content {
        order: 1;
    }

    .hero-booking {
        order: 2;
        justify-content: center;
    }

    .hero-subtitle {
        margin: 0 auto 2.5rem;
    }

    .hero-stats {
        justify-content: center;
    }

    .nav-links,
    .nav-buttons {
        display: none;
    }

    .mobile-menu-btn {
        display: flex;
    }

    .steps-container {
        flex-direction: column;
        gap: 2rem;
    }

    .step-connector {
        display: none;
    }

    .app-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .app-features {
        justify-content: center;
    }

    .app-buttons {
        justify-content: center;
    }

    .contact-grid {
        grid-template-columns: 1fr;
    }

    .footer-grid {
        grid-template-columns: 1fr 1fr;
    }
}

@media (max-width: 768px) {
    .hero-title {
        font-size: 2.5rem;
    }

    .section-title {
        font-size: 2rem;
    }

    .services-grid,
    .fleet-grid,
    .testimonials-slider,
    .destinations-grid {
        grid-template-columns: 1fr;
    }

    .features-grid {
        grid-template-columns: 1fr;
    }

    .comparison-grid {
        grid-template-columns: 1fr;
    }

    .corporate-features {
        grid-template-columns: 1fr;
    }

    .destination-card {
        flex-wrap: wrap;
    }

    .destination-price {
        width: 100%;
        text-align: left;
        margin-top: 0.5rem;
        padding-top: 0.75rem;
        border-top: 1px solid var(--gray-200);
    }

    .hero-stats {
        flex-direction: column;
        gap: 1.5rem;
    }

    .booking-card {
        margin: 0 1rem;
    }

    .cta-buttons {
        flex-direction: column;
        align-items: center;
    }

    .footer-grid {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .footer-brand p {
        margin: 0 auto 1.5rem;
    }

    .footer-badges {
        justify-content: center;
    }

    .footer-bottom {
        flex-direction: column;
        gap: 1rem;
    }

    .app-buttons {
        flex-direction: column;
        align-items: center;
    }
}

@media (max-width: 480px) {
    .form-row {
        grid-template-columns: 1fr;
    }

    .hero-title {
        font-size: 2rem;
    }

    .booking-card {
        padding: 1.5rem;
    }

    .partners-logos {
        gap: 1.5rem;
    }

    .partner-logo {
        font-size: 0.875rem;
    }
}

