/* ========================================
   SHUTTLE PLUS - Blog Page Styles
   ======================================== */

/* Blog Hero */
.blog-hero {
    position: relative;
    padding: 12rem 0 6rem;
    min-height: 70vh;
    display: flex;
    align-items: center;
    overflow: hidden;
}

.blog-hero-bg {
    position: absolute;
    inset: 0;
    background-image: url('../images/1600px-AddisView-1280x625.jpg');
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
}

.blog-hero-bg::before {
    content: '';
    position: absolute;
    inset: 0;
    background: linear-gradient(
        to bottom,
        rgba(24, 50, 81, 0.7) 0%,
        rgba(24, 50, 81, 0.5) 50%,
        rgba(24, 50, 81, 0.85) 100%
    );
}

.blog-hero-bg::after {
    content: '';
    position: absolute;
    inset: 0;
    background:
        radial-gradient(ellipse at 30% 50%, rgba(89, 123, 135, 0.3) 0%, transparent 50%),
        radial-gradient(ellipse at 70% 30%, rgba(0, 180, 216, 0.15) 0%, transparent 40%);
}

.blog-hero-content {
    position: relative;
    text-align: center;
    color: var(--white);
    z-index: 1;
}

.blog-hero-content .section-tag {
    display: inline-block;
    background: rgba(255, 255, 255, 0.2);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    color: var(--white);
    padding: 0.5rem 1.25rem;
    font-size: 0.9rem;
    font-weight: 600;
    letter-spacing: 0.05em;
    border-radius: var(--radius-full);
    margin-bottom: 1.25rem;
}

.blog-hero-content h1 {
    font-size: 4rem;
    font-weight: 800;
    margin-bottom: 1.5rem;
    text-shadow: 0 4px 30px rgba(0, 0, 0, 0.3);
    letter-spacing: -0.02em;
}

.blog-hero-content p {
    font-size: 1.35rem;
    color: rgba(255, 255, 255, 0.9);
    max-width: 650px;
    margin: 0 auto;
    text-shadow: 0 2px 10px rgba(0, 0, 0, 0.2);
    line-height: 1.6;
}

/* Blog Section */
.blog-section {
    padding: 4rem 0;
    background: var(--gray-50);
}

/* Featured Article */
.featured-article {
    display: grid;
    grid-template-columns: 1.2fr 1fr;
    gap: 3rem;
    background: var(--white);
    border-radius: var(--radius-2xl);
    overflow: hidden;
    box-shadow: var(--shadow-lg);
    margin-bottom: 3rem;
}

.featured-image {
    position: relative;
    min-height: 400px;
    background: linear-gradient(135deg, var(--primary-dark) 0%, var(--primary) 100%);
    display: flex;
    align-items: center;
    justify-content: center;
}

.featured-placeholder {
    font-size: 8rem;
    color: rgba(255, 255, 255, 0.2);
}

.article-badge {
    position: absolute;
    top: 1.5rem;
    left: 1.5rem;
    padding: 0.5rem 1rem;
    background: var(--accent);
    color: var(--white);
    font-size: 0.8125rem;
    font-weight: 600;
    border-radius: var(--radius-full);
}

.featured-content {
    padding: 3rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

.article-category {
    display: inline-flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.8125rem;
    font-weight: 600;
    color: var(--primary);
    margin-bottom: 1rem;
}

.featured-content h2 {
    font-size: 2rem;
    font-weight: 800;
    color: var(--dark);
    margin-bottom: 1rem;
    line-height: 1.3;
}

.featured-content p {
    font-size: 1rem;
    color: var(--gray-600);
    margin-bottom: 1.5rem;
    line-height: 1.7;
}

.article-meta {
    display: flex;
    gap: 1.5rem;
    margin-bottom: 1.5rem;
}

.article-meta span {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.875rem;
    color: var(--gray-500);
}

.article-meta i {
    color: var(--primary);
}

/* Blog Categories */
.blog-categories {
    display: flex;
    flex-wrap: wrap;
    gap: 0.75rem;
    justify-content: center;
    margin-bottom: 3rem;
}

.category-btn {
    padding: 0.75rem 1.5rem;
    font-size: 0.9375rem;
    font-weight: 500;
    color: var(--gray-600);
    background: var(--white);
    border: 2px solid var(--gray-200);
    border-radius: var(--radius-full);
    cursor: pointer;
    transition: var(--transition);
}

.category-btn:hover {
    border-color: var(--primary);
    color: var(--primary);
}

.category-btn.active {
    background: var(--primary);
    border-color: var(--primary);
    color: var(--white);
}

/* Blog Category Section */
.blog-category-section {
    margin-bottom: 4rem;
}

.section-header-left {
    margin-bottom: 2rem;
}

.section-header-left h2 {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    font-size: 1.75rem;
    font-weight: 800;
    color: var(--dark);
    margin-bottom: 0.5rem;
}

.section-header-left h2 i {
    color: var(--primary);
}

.section-header-left p {
    font-size: 1rem;
    color: var(--gray-500);
}

/* Articles Grid */
.articles-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 1.5rem;
}

.article-card {
    background: var(--white);
    border-radius: var(--radius-xl);
    overflow: hidden;
    box-shadow: var(--shadow);
    transition: var(--transition);
}

.article-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--shadow-lg);
}

.article-image {
    position: relative;
    height: 180px;
    background: linear-gradient(135deg, var(--gray-200) 0%, var(--gray-300) 100%);
    display: flex;
    align-items: center;
    justify-content: center;
}

.article-placeholder {
    font-size: 4rem;
    color: var(--gray-400);
}

.article-content {
    padding: 1.5rem;
}

.article-content h3 {
    font-size: 1.25rem;
    font-weight: 700;
    color: var(--dark);
    margin-bottom: 0.75rem;
}

.article-content > p {
    font-size: 0.9375rem;
    color: var(--gray-600);
    line-height: 1.6;
    margin-bottom: 1rem;
}

.article-details {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    margin-bottom: 1rem;
}

.article-details span {
    display: flex;
    align-items: center;
    gap: 0.375rem;
    font-size: 0.8125rem;
    color: var(--gray-500);
}

.article-details i {
    color: var(--primary);
    font-size: 0.75rem;
}

.article-tip {
    display: flex;
    align-items: flex-start;
    gap: 0.75rem;
    padding: 1rem;
    background: rgba(16, 185, 129, 0.1);
    border-radius: var(--radius);
    border-left: 3px solid var(--success);
}

.article-tip i {
    color: var(--success);
    font-size: 1rem;
    flex-shrink: 0;
    margin-top: 0.125rem;
}

.article-tip span {
    font-size: 0.8125rem;
    color: var(--gray-700);
    line-height: 1.5;
}

/* Tips Grid */
.tips-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 1.5rem;
}

.tip-card {
    background: var(--white);
    border-radius: var(--radius-xl);
    padding: 2rem;
    box-shadow: var(--shadow);
    text-align: center;
    transition: var(--transition);
}

.tip-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-md);
}

.tip-icon {
    width: 60px;
    height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(24, 50, 81, 0.1);
    border-radius: var(--radius-lg);
    margin: 0 auto 1rem;
    font-size: 1.5rem;
    color: var(--primary-dark);
}

.tip-card h3 {
    font-size: 1.125rem;
    font-weight: 700;
    color: var(--dark);
    margin-bottom: 0.75rem;
}

.tip-card p {
    font-size: 0.9375rem;
    color: var(--gray-600);
    line-height: 1.6;
}

/* Blog CTA */
.blog-cta {
    background: var(--gradient);
    border-radius: var(--radius-2xl);
    padding: 4rem;
    text-align: center;
    margin-top: 2rem;
}

.blog-cta-content h2 {
    font-size: 2rem;
    font-weight: 800;
    color: var(--white);
    margin-bottom: 1rem;
}

.blog-cta-content p {
    font-size: 1.125rem;
    color: rgba(255, 255, 255, 0.9);
    max-width: 600px;
    margin: 0 auto 2rem;
}

.blog-cta .btn-primary {
    background: var(--white);
    color: var(--primary-dark);
}

.blog-cta .btn-primary:hover {
    background: var(--gray-100);
}

/* Responsive */
@media (max-width: 1200px) {
    .articles-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .tips-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (max-width: 992px) {
    .featured-article {
        grid-template-columns: 1fr;
    }

    .featured-image {
        min-height: 300px;
    }

    .blog-hero {
        min-height: 60vh;
        padding: 10rem 0 5rem;
    }

    .blog-hero-content h1 {
        font-size: 2.75rem;
    }

    .blog-hero-content p {
        font-size: 1.15rem;
    }
}

@media (max-width: 768px) {
    .blog-hero {
        padding: 8rem 0 4rem;
        min-height: 55vh;
    }

    .blog-hero-content h1 {
        font-size: 2.25rem;
    }

    .blog-hero-content p {
        font-size: 1.05rem;
    }

    .articles-grid {
        grid-template-columns: 1fr;
    }

    .tips-grid {
        grid-template-columns: 1fr;
    }

    .featured-content {
        padding: 2rem;
    }

    .featured-content h2 {
        font-size: 1.5rem;
    }

    .blog-cta {
        padding: 3rem 2rem;
    }

    .blog-categories {
        gap: 0.5rem;
    }

    .category-btn {
        padding: 0.5rem 1rem;
        font-size: 0.8125rem;
    }
}

@media (max-width: 480px) {
    .article-meta {
        flex-direction: column;
        gap: 0.5rem;
    }

    .article-details {
        flex-direction: column;
        gap: 0.5rem;
    }
}
