   634→    function filterBookings() {
   635→        const statusFilter = document.getElementById('statusFilter')?.value;
   636→        const dateFilter = document.getElementById('dateFilter')?.value;
   637→
   638→        let filtered = [...bookings];
   639→
   640→        if (statusFilter) {
   641→            filtered = filtered.filter(b => b.status === statusFilter);
   642→        }
   643→
   644→        if (dateFilter === 'today') {
   645→            const today = new Date().toDateString();
   646→            filtered = filtered.filter(b => new Date(b.pickup?.scheduledTime).toDateString() === today);
   647→        } else if (dateFilter === 'tomorrow') {
   648→            const tomorrow = new Date();
   649→            tomorrow.setDate(tomorrow.getDate() + 1);
   650→            filtered = filtered.filter(b => new Date(b.pickup?.scheduledTime).toDateString() === tomorrow.toDateString());
   651→        }
   652→
   653→        // Re-render with filtered data
   654→        const originalBookings = bookings;
   655→        bookings = filtered;
   656→        renderBookingsTable();
   657→        bookings = originalBookings;
   658→    }
