   660→    function handleSearch(e) {
   661→        const query = e.target.value.toLowerCase();
   662→
   663→        if (!query) {
   664→            renderBookingsTable();
   665→            return;
   666→        }
   667→
   668→        const filtered = bookings.filter(b =>
   669→            b.bookingReference?.toLowerCase().includes(query) ||
   670→            b.customer?.name?.toLowerCase().includes(query) ||
   671→            b.customer?.phone?.includes(query)
   672→        );
   673→
   674→        const originalBookings = bookings;
   675→        bookings = filtered;
   676→        renderBookingsTable();
   677→        bookings = originalBookings;
   678→    }
   679→
   680→    // ========================================
   681→    // Modals
   682→    // ========================================
   683→    function closeModals() {
   684→        document.querySelectorAll('.modal').forEach(modal => {
   685→            modal.classList.remove('show');
   686→        });
   687→    }
   688→
   689→    function showNewBookingModal() {
   690→        showToast('New booking modal - redirecting to booking page', 'info');
   691→        window.open('/pages/booking.html', '_blank');
   692→    }
   693→
   694→    // ========================================
   695→    // Demo Data Generators
   696→    // ========================================
   697→    function generateDemoBookings() {
   698→        const statuses = ['pending', 'confirmed', 'driver_assigned', 'in_progress', 'completed'];
   699→        const destinations = [
   700→            'Sheraton Addis', 'Hilton Hotel', 'Skylight Hotel', 'Radisson Blu',
   701→            'Bole Atlas', 'Kazanchis', 'Piassa', 'Meskel Square', 'Mexico Area'
   702→        ];
   703→        const names = [
   704→            'John Smith', 'Sarah Johnson', 'Mohammed Ali', 'Elena Petrova',
   705→            'David Chen', 'Maria Garcia', 'James Brown', 'Anna Mueller'
   706→        ];
   707→
   708→        return Array.from({ length: 15 }, (_, i) => {
   709→            const pickupTime = new Date();
   710→            pickupTime.setHours(pickupTime.getHours() + Math.floor(Math.random() * 48) - 12);
   711→
   712→            return {
   713→                _id: `booking_${i}`,
   714→                bookingReference: `SP-2025-${String(Math.floor(Math.random() * 900000) + 100000).slice(0, 6)}`,
   715→                customer: {
   716→                    name: names[Math.floor(Math.random() * names.length)],
   717→                    phone: `+251 9${Math.floor(Math.random() * 90000000) + 10000000}`,
   718→                    email: `customer${i}@email.com`
   719→                },
   720→                type: Math.random() > 0.5 ? 'arrival' : 'departure',
   721→                flight: {
   722→                    number: `ET${Math.floor(Math.random() * 900) + 100}`,
   723→                    scheduledArrival: pickupTime.toISOString()
   724→                },
   725→                pickup: {
   726→                    location: 'Bole International Airport',
   727→                    scheduledTime: pickupTime.toISOString()
   728→                },
   729→                dropoff: {
   730→                    location: destinations[Math.floor(Math.random() * destinations.length)]
   731→                },
   732→                vehicleClass: ['standard', 'executive', 'suv', 'luxury'][Math.floor(Math.random() * 4)],
   733→                passengers: Math.floor(Math.random() * 4) + 1,
   734→                pricing: {
   735→                    totalUSD: Math.floor(Math.random() * 50) + 25,
   736→                    totalETB: Math.floor(Math.random() * 7000) + 3500
   737→                },
   738→                status: statuses[Math.floor(Math.random() * statuses.length)],
   739→                payment: {
   740→                    method: ['stripe', 'telebirr', 'cash'][Math.floor(Math.random() * 3)],
   741→                    status: Math.random() > 0.3 ? 'paid' : 'pending'
   742→                },
   743→                createdAt: new Date(Date.now() - Math.floor(Math.random() * 7 * 24 * 60 * 60 * 1000)).toISOString()
   744→            };
   745→        });
   746→    }
   747→
   748→    function generateDemoDrivers() {
   749→        const driverNames = [
   750→            'Abebe Kebede', 'Dawit Mengistu', 'Tekle Hailu', 'Solomon Tadesse',
   751→            'Yohannes Girma', 'Samuel Bekele', 'Daniel Alemu', 'Michael Tesfaye'
   752→        ];
   753→        const vehicles = [
   754→            { name: 'Toyota Corolla', type: 'standard' },
   755→            { name: 'Hyundai Accent', type: 'standard' },
   756→            { name: 'Toyota Camry', type: 'executive' },
   757→            { name: 'Toyota Land Cruiser', type: 'suv' },
   758→            { name: 'Mercedes E-Class', type: 'luxury' }
   759→        ];
   760→        const statuses = ['available', 'available', 'available', 'on_trip', 'offline'];
   761→
   762→        return driverNames.map((name, i) => {
   763→            const vehicle = vehicles[i % vehicles.length];
   764→            return {
   765→                id: `driver_${i}`,
   766→                name,
   767→                phone: `+251 9${Math.floor(Math.random() * 90000000) + 10000000}`,
   768→                vehicle: vehicle.name,
   769→                vehicleType: vehicle.type,
   770→                plate: `3-AA-${String(Math.floor(Math.random() * 90000) + 10000)}`,
   771→                status: statuses[Math.floor(Math.random() * statuses.length)],
   772→                trips: Math.floor(Math.random() * 500) + 50,
   773→                rating: (Math.random() * 1 + 4).toFixed(1),
   774→                earnings: Math.floor(Math.random() * 200) + 50
   775→            };
   776→        });
   777→    }
   778→
   779→    function generateDemoCustomers() {
   780→        const names = [
   781→            'John Smith', 'Sarah Johnson', 'Mohammed Ali', 'Elena Petrova',
   782→            'David Chen', 'Maria Garcia', 'James Brown', 'Anna Mueller',
   783→            'Robert Wilson', 'Lisa Anderson'
   784→        ];
   785→
   786→        return names.map((name, i) => ({
   787→            id: `customer_${i}`,
   788→            name,
   789→            phone: `+251 9${Math.floor(Math.random() * 90000000) + 10000000}`,
   790→            email: i % 2 === 0 ? `${name.toLowerCase().replace(' ', '.')}@email.com` : null,
   791→            totalTrips: Math.floor(Math.random() * 20) + 1,
   792→            totalSpent: Math.floor(Math.random() * 1000) + 100,
   793→            lastTrip: new Date(Date.now() - Math.floor(Math.random() * 30 * 24 * 60 * 60 * 1000)).toISOString()
   794→        }));
   795→    }
   796→
   797→    // ========================================
   798→    // Utility Functions
   799→    // ========================================
   800→    function updatePendingBadge() {
   801→        const pending = bookings.filter(b => b.status === 'pending' || b.status === 'confirmed').length;
   802→        document.getElementById('pendingBadge').textContent = pending;
   803→    }
   804→
   805→    function formatStatus(status) {
   806→        const statusMap = {
   807→            pending: 'Pending',
   808→            confirmed: 'Confirmed',
   809→            driver_assigned: 'Driver Assigned',
   810→            in_progress: 'In Progress',
   811→            completed: 'Completed',
   812→            cancelled: 'Cancelled',
   813→            available: 'Available',
   814→            on_trip: 'On Trip',
   815→            offline: 'Offline'
   816→        };
   817→        return statusMap[status] || status;
   818→    }
   819→
   820→    function formatTime(dateString) {
   821→        if (!dateString) return '-';
   822→        return new Date(dateString).toLocaleTimeString('en-US', {
   823→            hour: '2-digit',
   824→            minute: '2-digit',
   825→            hour12: true
   826→        });
   827→    }
   828→
   829→    function formatDate(dateString) {
   830→        if (!dateString) return '-';
   831→        return new Date(dateString).toLocaleDateString('en-US', {
   832→            month: 'short',
   833→            day: 'numeric'
   834→        });
   835→    }
   836→
   837→    function formatDateTime(dateString) {
   838→        if (!dateString) return '-';
   839→        return new Date(dateString).toLocaleString('en-US', {
   840→            month: 'short',
   841→            day: 'numeric',
   842→            hour: '2-digit',
   843→            minute: '2-digit',
   844→            hour12: true
   845→        });
   846→    }
   847→
   848→    function truncate(str, length) {
   849→        if (!str) return '-';
   850→        return str.length > length ? str.substring(0, length) + '...' : str;
   851→    }
   852→
   853→    function debounce(func, wait) {
   854→        let timeout;
   855→        return function executedFunction(...args) {
   856→            const later = () => {
   857→                clearTimeout(timeout);
   858→                func(...args);
   859→            };
   860→            clearTimeout(timeout);
   861→            timeout = setTimeout(later, wait);
   862→        };
   863→    }
   864→
   865→    function showToast(message, type = 'info') {
   866→        const toast = document.createElement('div');
   867→        toast.className = `toast toast-${type}`;
   868→        toast.innerHTML = `
   869→            <i class="fas fa-${type === 'success' ? 'check-circle' : type === 'error' ? 'exclamation-circle' : 'info-circle'}"></i>
   870→            <span>${message}</span>
   871→        `;
   872→        toast.style.cssText = `
   873→            position: fixed;
   874→            bottom: 20px;
   875→            right: 20px;
   876→            background: ${type === 'success' ? '#22c55e' : type === 'error' ? '#ef4444' : '#3b82f6'};
   877→            color: white;
   878→            padding: 1rem 1.5rem;
   879→            border-radius: 8px;
   880→            display: flex;
   881→            align-items: center;
   882→            gap: 0.5rem;
   883→            z-index: 9999;
   884→            animation: slideIn 0.3s ease;
   885→        `;
   886→
   887→        document.body.appendChild(toast);
   888→
   889→        setTimeout(() => {
   890→            toast.style.animation = 'slideOut 0.3s ease';
   891→            setTimeout(() => toast.remove(), 300);
   892→        }, 3000);
   893→    }
   894→
   895→    // Add animation styles
   896→    const style = document.createElement('style');
   897→    style.textContent = `
   898→        @keyframes slideIn {
   899→            from { transform: translateX(100%); opacity: 0; }
   900→            to { transform: translateX(0); opacity: 1; }
   901→        }
   902→        @keyframes slideOut {
   903→            from { transform: translateX(0); opacity: 1; }
   904→            to { transform: translateX(100%); opacity: 0; }
   905→        }
   906→    `;
   907→    document.head.appendChild(style);
   908→
   909→    // ========================================
   910→    // Initialize on Load
   911→    // ========================================
   912→    if (document.readyState === 'loading') {
   913→        document.addEventListener('DOMContentLoaded', init);
   914→    } else {
   915→        init();
   916→    }
   917→
   918→})();
